import React, { useState, useEffect } from "react";
import "./Frontpage.css";
import logo from "./logo.png";
import logo1 from "./logo1.png";
import google_icon from "./google_icon.png";
import Particles from "react-particles-js";
import Footer from "./Footer";
import { GoogleLogin } from "react-google-login";
import { useDispatch } from "react-redux";
import { useHistory, useLocation } from "react-router-dom";

const Frontpage = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();

  const googleSuccess = async (res) => {
    const result = res?.profileObj;
    const token = res?.tokenId;

    try {
      dispatch({ type: "AUTH", data: { result, token } });
      history.push("/home");
    } catch (error) {
      console.log(error);
    }
  };

  const googleFailure = () => {
    console.log("Google sign In was unsuccessful. Try again later");
    alert("LOGIN UNSUCCESSFUL. CHECK CREDENTIALS");
  };

  useEffect(() => {
    const token = user?.token;
    setUser(JSON.parse(localStorage.getItem("profile")));
  }, [location]);

  return (
    <div className="front-page-container">
      <div className="container">
        <div className="logo-section">
          <img src={logo1} alt="logo1" className="nav-logo" />
          <div className="main-logo-div">
            <img src={logo} alt="logo" className="main-logo" />
            <p className="main-text">
              An engineering solution to meeting friends, family or work, free
              and easy to use.
            </p>
            <GoogleLogin
              clientId="your-client-id"
              render={(renderProps) => (
                <button
                  className="google-login-button"
                  onClick={renderProps.onClick}
                  disabled={renderProps.disabled}
                >
                  <img src={google_icon} alt="" className="g-icon" />
                  Sign in with Google
                </button>
              )}
              onSuccess={googleSuccess}
              onFailure={googleFailure}
              cookiePolicy="single_host_origin"
            />
            <hr className="rule" />
            <p className="learn-more">
              <span style={{ color: "#44D096" }}>Learn More</span> about ZenTalk
            </p>
          </div>
        </div>
        <div className="particles-section">
          <Particles
            params={{
              // Your particle settings here
            }}
          />
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Frontpage;
